"use strict";

var P = require('bluebird');

var logger = require('../../../services/logger');

var dataUtil = require('../../../utils/data');

function SubscriptionsGetter(Implementation, params, opts, integrationInfo) {
  var stripe = opts.integrations.stripe.stripe(opts.integrations.stripe.apiKey);
  var collectionModel = null;

  function hasPagination() {
    return params.page;
  }

  function getLimit() {
    if (hasPagination()) {
      return params.page.size || 10;
    }

    return 10;
  }

  function getStartingAfter() {
    if (hasPagination() && params.starting_after) {
      return params.starting_after;
    }

    return undefined;
  }

  function getEndingBefore() {
    if (hasPagination() && params.ending_before) {
      return params.ending_before;
    }

    return undefined;
  }

  function getSubscriptions(query) {
    return new P(function (resolve, reject) {
      stripe.subscriptions.list(query, function (err, subcriptions) {
        if (err) {
          return reject(err);
        }

        return resolve([subcriptions.total_count, subcriptions.data]);
      });
    });
  }

  this.perform = function () {
    collectionModel = integrationInfo.collection;
    var collectionFieldName = integrationInfo.field,
        embeddedPath = integrationInfo.embeddedPath;
    var fieldName = embeddedPath ? "".concat(collectionFieldName, ".").concat(embeddedPath) : collectionFieldName;
    return Implementation.Stripe.getCustomer(collectionModel, collectionFieldName, params.recordId).then(function (customer) {
      var query = {
        limit: getLimit(),
        starting_after: getStartingAfter(),
        ending_before: getEndingBefore(),
        'include[]': 'total_count'
      };

      if (customer && !!customer[collectionFieldName]) {
        query.customer = dataUtil.find(customer[collectionFieldName], embeddedPath);
      }

      if (customer && !query.customer) {
        return P.resolve([0, []]);
      }

      return getSubscriptions(query).spread(function (count, subscriptions) {
        return P.map(subscriptions, function (subscription) {
          if (customer) {
            subscription.customer = customer;
          } else {
            return Implementation.Stripe.getCustomerByUserField(collectionModel, fieldName, subscription.customer).then(function (customerFound) {
              subscription.customer = customerFound;
              return subscription;
            });
          }

          return subscription;
        }).then(function (subscriptionsData) {
          return [count, subscriptionsData];
        });
      })["catch"](function (error) {
        logger.warn('Stripe subscriptions retrieval issue: ', error);
        return P.resolve([0, []]);
      });
    }, function () {
      return P.resolve([0, []]);
    });
  };
}

module.exports = SubscriptionsGetter;