"use strict";

exports.find = function (data, path) {
  if (data) {
    if (path) {
      var keys = path.split('.');
      var value = data; // eslint-disable-next-line consistent-return

      keys.forEach(function (key) {
        if (key in value && value[key]) {
          value = value[key];
        } else {
          value = null;
        }
      });
      return value;
    }
  }

  return data;
};